import React from 'react'

function RecentActivityItem(item) {
    const { time, color, content = '', highlight = '' } = item;
  return (
    // <div className='activity-item d-flex'>
    //     <div className='activity-label'>{item.time}</div>
    //     <i className={`bi bi-circle-fill activity-badge ${item.color}`}></i>

    //     {
    //         item.highlight == '' ?(
    //             <div className='activity-content'>{item.content}</div>
    //         ): (
    //         <div className='activity-content'>
    //         {
    //             item.content.substring(0, item.content.indexOf(item.highlight))
    //         }
    //         <a href='#' className='fw-bold text-dark'>
    //             {item.highlight}
    //         </a>

    //         {item.content.slice(item.content.indexOf(item.highlight)+ item.highlight.length,-1)}

    //     </div>
    //         )}
    // </div>
    
    <div className='activity-item d-flex'>
    <div className='activity-label'>{time}</div>
    <i className={`bi bi-circle-fill activity-badge ${color}`}></i>

    {
        highlight === '' ? (
            <div className='activity-content'>{content}</div>
        ) : (
            <div className='activity-content'>
                {
                    content.substring(0, content.indexOf(highlight))
                }
                <a href='#' className='fw-bold text-dark'>
                    {highlight}
                </a>
                {
                    content.slice(content.indexOf(highlight) + highlight.length)
                }
            </div>
        )
    }
</div>
  
);
}

export default RecentActivityItem
