import { useEffect,useState } from 'react';

import CardFilter from '../cardcomponent/CardFilter'
import RecentActivityItem from './RecentActivityItem';
import './recentActivity.css'

function Recentactivity() {
//     

const [filter, setFilter] = useState('Today');
    
    // Hard-coded items
    const items = [
        {
            _id: '1',
            time: '10:00 AM',
            color: 'bg-success',
            content: 'Completed task A',
            highlight: ''
        },
        {
            _id: '2',
            time: '12:30 PM',
            color: 'bg-warning',
            content: 'Started task B with <strong>high priority</strong>',
            highlight: 'high priority'
        },
        {
            _id: '3',
            time: '3:15 PM',
            color: 'bg-danger',
            content: 'Failed task C',
            highlight: ''
        }
    ];

    const handleFilterChange = filter => {
        setFilter(filter);
    };

    return (
        <div className='card'>
            <CardFilter filterChange={handleFilterChange} />
            <div className='card-body'>
                <h5 className='card-title'>
                    Recent Activity <span>{filter}</span>
                </h5>

                <div className='activity'>
                    {
                        items && items.length > 0 &&
                        items.map(item => (<RecentActivityItem key={item._id} item={item}/>))
                    }
                </div>
            </div>
        </div>
    );
}

export default Recentactivity
